import React, { Component } from 'react';
import * as api from '../api'


class ArticleVotesChange extends Component {
  state = {
    vote: 0,
    error: {
      status: null,
      msg: '',
    },
  }
  render() {
    const {status, msg} = this.state.error

    return (
      <div>
        votes: {this.props.article_vote + +this.state.vote}
        <br/>
            <button className="like" value="1" onClick={this.handleClick}>
              Like
            </button>
            <button className="dislike" value="-1" onClick={this.handleClick}>
              Dislike
            </button>
            <button className="cancelVote" onClick={this.handleCancel}>
              Remove Vote
            </button>
            {(this.state.error.status !== null) && <p>Error {status}! {msg}</p>}
      </div>
    );
  }
  // handleLike = ()=> {
  //   this.setState({vote: 1})
  // }

  // handleDislike = () => {
  //   this.setState({vote: -1})
  // }

  handleClick = (event) => {
    this.setState({vote: event.target.value})
  }

  handleCancel = (event) => {
    this.setState(currentState => {
      return {vote: -(+currentState.vote)}
    })
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.vote !== this.state.vote){
      const {article_id} = this.props
      const {vote} = this.state
      api.updateArticleVotes(article_id, vote)
      .catch(error => {
        return(

          this.setState({error:{status: 500, msg: 'voting did not work, please try again later'}}))

      })        
    }
  }
}

export default ArticleVotesChange;
