import React, { Component } from 'react';
import {Link} from '@reach/router'
import ArticleCard from './ArticleCard';
import * as api from '../api';

class ArticlesList extends Component {
  state = {
    articles: [],
    author: '',
    topic: '',
    sort_by: '',
    order: '',
    isLoading: true,
  }
  render() {
    if (this.state.isLoading){
      return <img src="https://upload.wikimedia.org/wikipedia/commons/b/b1/Loading_icon.gif" alt="loading..."/>
    } 

    return (
      <div>
        <ul id = "article-list">
          {this.state.articles.map(article => {
            return (
              <Link key={article.article_id} to={`/${article.article_id}`}> 
                <ArticleCard article={article} />
              </Link>

            )


          })}
        </ul>
      </div>
    );
  }


  componentDidMount() {
    this.fetchArticles()
  }

  componentDidUpdate(prevProps) {
    console.log(this.props)
    if (prevProps.topicName !== this.props.topicName)
    this.fetchArticles()
  }

  fetchArticles() {
    const topicName = this.props.topicName
    api.getAllArticles(topicName)
    .then(({data: {articles}}) => {
      this.setState({articles, isLoading: false, topic: topicName})
    })
  }
}

export default ArticlesList;
