import React, { Component } from 'react';
import * as api from '../api'

class CommentVotesChange extends Component {

  state = {
    vote: 0,
    inputsDisabled: true,
  }

  render() {
    console.log('props', this.props)

    return (
      <div>
         votes: {this.props.comment_vote + this.state.vote}
         <br/>
            <button className="like" onClick={this.handleCli}>
              Like
            </button>        
            <button className="dislike" onClick={this.handleDislike}>
              Dislike             
            </button>      
      </div>
    );
  }

  // handleLike = ()=> {
  //   this.setState({vote: 1})
  // }

  // handleDislike = () => {
  //   this.setState({vote: -1})
  // }

  handleClick = (event) => {
    this.setState({vote: event.target.value})
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.vote !== this.state.vote){
      const {comment_id} = this.props
      const {vote} = this.state
      api.updateCommentVotes(comment_id, vote)        
    }
  }
}

export default CommentVotesChange;
