import React, { Component } from "react";
import * as api from "../api";
import up from "../up.svg";
import down from "../down.svg";

class ArticleVotesChange extends Component {
  state = {
    vote: 0,
    error: {
      status: null,
      msg: ""
    }
  };
  render() {
    const { status, msg } = this.state.error;

    return (
      <div>
        votes: {this.props.article_vote + +this.state.vote}
        <br />
        <button
          className="like"
          value={1}
          onClick={this.handleClick}
          disabled={this.state.vote === 1 ? true : false}
        >
          Like
          {/* <img src={up} alt="Home" id="vote-img"/> */}
        </button>
        <button
          className="dislike"
          value={-1}
          onClick={this.handleClick}
          disabled={this.state.vote === -1 ? true : false}
        >
          {/* <img src={down} alt="Home" id="vote-img"/> */}Dislike
        </button>
        <br />
        <button
          className="cancel"
          value={0}
          onClick={this.handleClick}
          disabled={this.state.vote === 0 ? true : false}
        >
          Cancel Vote
        </button>
        {status !== null && (
          <p>
            Error {status}! {msg}
          </p>
        )}
      </div>
    );
  }

  handleClick = event => {
    this.setState({ vote: event.target.value });
  };

  componentDidUpdate(prevProps, prevState) {
    if (this.state.vote === 0){
      const { article_id } = this.props;
      //const {vote} = prevState.vote
      console.log(prevState.vote)
      api.updateArticleVotes(article_id, ).catch(error => {
        return this.setState({
          vote: 0,
          error: {
            status: 500,
            msg: "voting did not work, please try again later"
          }
        });
      });
    }

    else if (prevState.vote !== this.state.vote) {
      console.log('prev', prevState.vote, "this", this.state.vote);
      const { article_id } = this.props;
      const { vote } = this.state;
      api.updateArticleVotes(article_id, vote).catch(error => {
        return this.setState({
          vote: 0,
          error: {
            status: 500,
            msg: "voting did not work, please try again later"
          }
        });
      });
    }
  }
}

export default ArticleVotesChange;
