import React, { Component } from 'react';
import * as api from '../api';

class CommentAdder extends Component {
  state = { 
    body: "",
    postedComment: []
  }
  render() {

    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>
            Comment:
            <input type="text" required placeholder="What do you think?" onChange = {this.handleChange}/>
          </label>
          <button >Post</button>
        </form>
      </div>
    );
  }

  handleChange = (event) => {
    this.setState({body: event.target.value})
  }
  handleSubmit = (event) => {
    event.preventDefault();
    const {article_id, user} = this.props
    const {body} = this.state
    api.addComment(article_id, user, body).then(({data:{comment}}) => {
      return (
        <>
        {console.log(comment)}
        <p>Comment Successfully Created at {comment.created_at}{comment.body}</p>
        </>
      )
    })
    this.setState({body: ''})
  }
}

export default CommentAdder;
