import React, { Component } from 'react';

class CommentAdder extends Component {
  render() {
    return (
      <div>
        <form>
          <label>
            Comment:
            <input type="text" required placeholder="What do you think?"/>
          </label>
          <button>Post</button>
        </form>
      </div>
    );
  }
}

export default CommentAdder;
