import React, { Component } from 'react';
import * as api from '../api.js'

class SingleArticle extends Component {
  state = {
    article: [],
    isLoading: true,
  };
  render() {
    console.log('in single article', this.props);
    if (this.state.isLoading){
      return <img src="https://upload.wikimedia.org/wikipedia/commons/b/b1/Loading_icon.gif" alt="loading..."/>
    } 
    return (
      <div>

      </div>
    );
  }
  fetchData() {
    api.getSingleArticle(this.props.article_id).then(response => console.log(response))
  }
  componentDidMount(){
    this.fetchData();
  }
}

export default SingleArticle;
