import React, { Component } from 'react';
import ArticlesList from './ArticlesList';

class ArticlesByTopic extends Component {
  state = {
    topic: ''
  }
  render() {
    return (
      <main>
        <h2> {this.props.topicName} </h2>
        <ArticlesList />
      </main>
    );
  }
}

export default ArticlesByTopic;
