import React, { Component } from 'react';
import * as api from '../api'

class CommentVotesChange extends Component {

  state = {
    vote: 0
  }
  render() {
    console.log(this.state.vote)
    return (
      <div>
        <ul>
          <li className="vote">
            <label className="like" onClick={this.handleLike}>
              Like
              <input type='radio' value='like' name='commentvote' id='like'/>
            </label>
            <li className="vote">
            <label className="dislike" nClick={this.handleDislike}>
              Dislike
              <input type='radio' value='dislike' name='commentvote' id='dislike'/>
            </label>
          </li>
          </li>
        </ul>
      </div>
    );
  }

  handleLike = ()=> {
    this.setState({vote: 1})
  }

  handleDislike = () => {
    this.setState({vote: -1})
  }

  // componentDidUpdate() {
  //   api.updateCommentVotes
  // }
}

export default CommentVotesChange;
