import React, { Component } from 'react';
import * as api from '../api'
import ErrorPage from './ErrorPage';

class ArticleVotesChange extends Component {
  state = {
    vote: 0,
    error: {
      status: null,
      msg: '',
    },
  }
  render() {
    const {status, msg} = this.state.error

    return (
      <div>
        votes: {this.props.article_vote + this.state.vote}
        <br/>
            <button className="like" onClick={this.handleLike}>
              Like
            </button>
            <button className="dislike" onClick={this.handleDislike}>
              Dislike
            </button>

            {(this.state.error.status !== null) && <p>Error {status}! {msg}</p>}
      </div>
    );
  }
  handleLike = ()=> {
    this.setState({vote: 1})
  }

  handleDislike = () => {
    this.setState({vote: -1})
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.vote !== this.state.vote){
      const {article_id} = this.props
      const {vote} = this.state
      api.updateArticleVotes(article_id, vote).catch(error => {
        //const {status, msg} = error
        this.setState({status: 500, msg: 'voting did not work, please try again later'})
      })        
    }
  }
}

export default ArticleVotesChange;
